// src/pages/ProductPage.jsx
import React, { useEffect, useState } from 'react';
import { Link, useNavigate }      from 'react-router-dom';
// import api                     from '../services/api';   // no API for sample
import sampleProducts            from '../data/sampleProducts';
import ProductCard               from '../components/product/ProductCard';
import '../assets/css/ProductPage.css';

export default function ProductPage({ createMode = false }) {
  const [products, setProducts] = useState([]);
  const navigate                = useNavigate();
  const token                   = localStorage.getItem('token');

  // Load sample products instead of API
  useEffect(() => {
    setProducts(sampleProducts);
  }, []);

  // rest of your createMode logic...
  // (you can leave the createMode form as-is or hide it)
  return (
    <div className="product-page">
      <header className="product-header">
        <h1>Our Products</h1>
        { !createMode && token && (
          <Link to="/products/new" className="btn-new">+ New Product</Link>
        )}
      </header>

      <div className="product-grid">
        {products.map(p => (
          <ProductCard key={p.id} product={p} />
        ))}
      </div>
    </div>
  );
}
