// src/pages/RegistrationPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../assets/css/RegistrationPage.css';

function RegistrationPage() {
  const [form, setForm] = useState({
    username: '', password: '', email: '', phone: '', firstName: '', lastName: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      await api.post('/auth/register', form);

      // show a big, visible toast
      toast.success('üéâ Registration successful!', {
        position: 'top-center',
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
      });

      // after a short delay, redirect to login
      setTimeout(() => navigate('/login'), 3500);
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
      toast.error(`‚ùå ${err.response?.data?.message || 'Registration failed'}`, {
        position: 'top-center',
        autoClose: 5000,
      });
    }
  };

  return (
    <div className="registration-container">
      {/* ToastContainer can live anywhere once per app */}
      <ToastContainer />

      <h2>Create an Account</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit} className="registration-form">
        {['username','password','email','phone','firstName','lastName'].map(field => (
          <label key={field}>
            {field[0].toUpperCase()+field.slice(1)}
            <input
              name={field}
              type={field === 'password' ? 'password' : 'text'}
              value={form[field]}
              onChange={onChange}
              required={field !== 'phone'}
            />
          </label>
        ))}
        <button type="submit">Create Account</button>
      </form>
    </div>
  );
}

export default RegistrationPage;
